import socket

HOST = 'challenge.ctf.games'
PORT = 32287

def solve_math_question(question):
    parts = question.split()
    num1 = float(parts[2])
    operator = parts[3]
    num2 = float(parts[4].replace('?', ''))

    if operator == '/':
        if num2.is_integer():
            return str(int(num1 // num2))
        else:
            return str(round(num1 / num2, 1))
    elif operator == '+':
        return str(round(num1 + num2, 1))
    elif operator == '-':
        return str(round(num1 - num2, 1))
    elif operator == '*':
        return str(round(num1 * num2, 1))
    else:
        return "Invalid operator"

def main():
    with socket.socket(socket.AF_INET, socket.SOCK_STREAM) as s:
        s.connect((HOST, PORT))

        try:
            while True:
                data = s.recv(1024).decode('utf-8')

                if "Do you want to give it a chance? (Y/n):" in data:
                    s.send(b'Y
')
                elif "What is" in data:
                    question = data.strip()
                    answer = solve_math_question(question)
                    s.send(f'{answer}
'.encode('utf-8'))
                elif "Awesome, good luck!" in data:
                    continue
                elif "flag" in data:
                    print(data)
                    break
                else:
                    if "Wrong!" not in data:
                        print(data)
                    continue
        except KeyboardInterrupt:
            print("Interrupted. Good bye!")

if __name__ == '__main__':
    main()
